import React, { useState, useEffect } from 'react';

import './dashboard.sass'


const Dashboard = () => {

  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/books')
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data)
        setData(data)
      })
  }, []);

  return (
    <div className="dashboard">
      <h1>Book List</h1>
      <table className='table'>
        <thead>
          <tr>
            <th>Book title</th>
            <th>Author name</th>
            <th>Category</th>
            <th>ISBN</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            return (
              <tr key={index}>
                <th>{item.title}</th>
                <th>{item.author}</th>
                <th>{item.category}</th>
                <th>{item.isbn}</th>
                <th>
                  <button>Ed</button>
                  <button>Del</button>
                </th>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default Dashboard
