import React, { useState } from 'react';
import Modal from 'react-modal';
import useFetch from '../hooks/useFetch';

import Dashboard from '../Dashboard/index'

import './home.sass'


const Home = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [deletedId, setDeletedId] = useState(null);
  const [deletedTitle, setDeletedTitle] = useState(null);
  const [updateFlag, setUpdateFlag] = useState(false)


  const confirmDelete = (id, title) => {
    setDeletedId(id);
    setDeletedTitle(title);
    setModalIsOpen(true);
  }

  const cancelDelete = () => {
    setModalIsOpen(false);
    setDeletedTitle(null);
    setDeletedId(null);
  }

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/books/${id}`, {
      method: 'DELETE'
    }).then(() => {
      setUpdateFlag(prevUpdateFlag => !prevUpdateFlag);
    });
    setModalIsOpen(false);
    setDeletedTitle(null);
    setDeletedId(null); 
  }

  const { data: books, isPending, error } = useFetch('http://localhost:8000/books', updateFlag);

  return (
    <div className="dashboard">
      {error && <div className='error'>{error}</div>}
      {isPending && <div className='loading'>Loading...</div>}
      {books && <Dashboard books={books} confirmDelete={confirmDelete} />}
      <Modal
        isOpen={modalIsOpen}
        style={{
          overlay: {
            position: 'fixed',
            top: 0,
            left: 0,
            backgroundColor: '#8aabb499'
          },
          content: {
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            maxWidth: '600px',
            height: '200px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            border: '1px solid #ccc',
            background: '#fff',
            overflow: 'auto',
            WebkitOverflowScrolling: 'touch',
            borderRadius: '4px',
            outline: 'none',
            padding: '20px'
          }
        }}
      >
        <h2 className='modal__header'>Delete {deletedTitle} ?</h2>
        <div className="modal__buttons">
          <button onClick={() => cancelDelete()}>No</button>
          <button onClick={() => handleDelete(deletedId)}>Yes</button>
        </div>
      </Modal>
    </div>
  )
}

export default Home
