import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import Modal from 'react-modal';

import './dashboard.sass';

Modal.setAppElement('#root');

const Dashboard = ({ books }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [deletedId, setDeletedId] = useState(null);
  const [deletedTitle, setDeletedTitle] = useState(null);
  const { push } = useHistory();

  const confirmDelete = (id, title) => {
    setDeletedId(id);
    setDeletedTitle(title);
    setModalIsOpen(true);
  }

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/books/${id}`, {
      method: 'DELETE'
    })
  }




  return (
    <div>
      <table className='table'>
        <thead>
          <tr>
            <th>Book title</th>
            <th>Author name</th>
            <th>Category</th>
            <th>ISBN</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((item) => {
            return (
              <tr key={item.id}>
                <th>{item.title}</th>
                <th>{item.author}</th>
                <th>{item.category}</th>
                <th>{item.isbn}</th>
                <th>
                  <button
                    type="button"
                    onClick={() => push(`/edit/${item.id}`)}>
                    Edit
                  </button>
                  <button
                    onClick={() => confirmDelete(item.id, item.title)}>Del</button>
                </th>
              </tr>
            )
          })}
        </tbody>
      </table>

      <Modal
        isOpen={modalIsOpen}
        portalClassName='portal-modal'
        overlayClassName="overlay-modal"
        className="modal"
      >
        <h2 className='modal__header'>Delete {deletedTitle} ?</h2>
        <div className="modal__buttons">
          <button onClick={() => { setModalIsOpen(false); setDeletedTitle(null); setDeletedId(null) }}>No</button>
          <button onClick={() => { setModalIsOpen(false); setDeletedTitle(null); setDeletedId(null); handleDelete(deletedId) }}>Yes</button>
        </div>
      </Modal>
    </div>
  )
}

export default Dashboard
