import './home.sass'


const Home = () => {
  const[needUpdate, setNeedUpdate] = use

  const { data: books, isPending, error } = useFetch('http://localhost:8000/books');

  useEffect(() => {
    console.log(`home ---------------->`)
  }, [])

  return (
    <div className="dashboard">
      { error && <div>{error}</div> }
      { isPending && <div>Loading...</div> }
      { books && <Dashboard books={books} /> }
    </div>
  )
}

export default Home
