import React, { createContext, useState, useEffect } from 'react';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {

    const [data, setData] = useState([]);




    return (
        <UserContext.Provider
            value={{
                data,
                setData
            }}
        >
            {children}
        </UserContext.Provider>
    );
};
