import React from 'react';
import useWindowDimensions from '../hooks/useWindowDimensions'
import { useHistory } from 'react-router-dom';
import { ReactComponent as EditIcon } from '../icons/edit.svg';
import { ReactComponent as DeleteIcon } from '../icons/delete.svg';

import './dashboard.sass';

const Dashboard = ({ books, confirmDelete }) => {
  const { width } = useWindowDimensions();
  const { push } = useHistory();

  return (
    <table className='table'>
      <thead className='table__header'>
        <tr className='table__header__row'>
          <th className='table__header__col'>Title</th>
          <th className='table__header__col'>Author</th>
          <th className='table__header__col'>Category</th>
          <th className='table__header__col'>ISBN</th>
          <th className='table__header__col'>Actions</th>
        </tr>
      </thead>
      <tbody className='table__body'>
        {books.map((item) => {
          return (
            <tr className='table__body__row' key={item.id}>
              <th className='table__body__col'><span className='table__body__col'>Title</span>{item.title}</th>
              <th className='table__body__col'><span className='table__body__col'>Title</span>{item.author}</th>
              <th className='table__body__col'><span className='table__body__col'>Title</span>{item.category}</th>
              <th className='table__body__col'><span className='table__body__col'>Title</span>{item.isbn}</th>
              <th className='table__body__col table__buttons'>
                <button
                  className='table__button table__button--edit'
                  type="button"
                  onClick={() => push(`/edit/${item.id}`)}>
                   <EditIcon />
                </button>
                <button
                  className='table__button table__button--delete'
                  onClick={() => confirmDelete(item.id, item.title)}>
                  <DeleteIcon />
                </button>
              </th>
            </tr>
          )
        })}
      </tbody>
    </table>
  )
}

export default Dashboard
