import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import useFetch from '../hooks/useFetch';

import './edit-book.sass';

const EditBook = () => {

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [category, setCategory] = useState('Classics');
  const [isbn, setIsbn] = useState('');

  const history = useHistory();
  const { id } = useParams();
  const { data: bookList } = useFetch(`http://localhost:8000/books/`)
  const { data: book, error, isPending } = useFetch(`http://localhost:8000/books/${id}`)

  useEffect(() => {
    if (book) {
      const { title, author, category, isbn } = book;


      setTitle(title);
      setAuthor(author);
      setCategory(category);
      setIsbn(isbn);
    }

  }, [book])

  const handleSubmit = (e) => {
    e.preventDefault();

    const editedBook = { title, author, category, isbn };
// console.log(id)
//     const newBookList = bookList.map(item =>
//       item.id === id ? console.log(book.id + '   ' + id) : item
//     );

//     console.log(bookList);


    fetch(`http://localhost:8000/books/${id}`, {
      method: 'PUT',
      body: JSON.stringify(editedBook)
    }).then(() => {
      history.push('/')
    })

  }

  console.log(id)
  return (
    <div className='add-book'>
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {book && (
        <form className='form' onSubmit={handleSubmit}>
          <label>Book title</label>
          <input
            type="text"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <label for="author">Author name</label>
          <input
            type="text"
            id='author'
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
          />
          <label>Category</label>
          <select
            value={category}
            onChange={((e) => setCategory(e.target.value))}
          >
            <option value="classics">Classics</option>
            <option value="detective">Detective</option>
            <option value="historical">Historical</option>
          </select>
          <label>ISBN</label>
          <input
            type="text"
            name="isbn"
            value={isbn}
            onChange={(e) => setIsbn(e.target.value)}
          />
          <button type="submit">Save</button>
        </form>
      )}
    </div>
  )
}

export default EditBook
