import React, { useEffect, useState } from 'react';
import useFetch from '../hooks/useFetch';
import api from '../Api/books';
import Modal from 'react-modal';

import Dashboard from '../Dashboard/index'

import './home.sass'


const Home = () => {

  const [books, setBooks] = useState(null);
  const [isPending, setIsPending] = useState([]);
  const [error, setError] = useState([]);

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [deletedId, setDeletedId] = useState(null);
  const [deletedTitle, setDeletedTitle] = useState(null);

  useEffect(() => {
    retriveContacts()
  }, [books])

  const retriveContacts = async () => {
    const response = fetch(`http://localhost:8000/books`)
      .then(res => {
        if (!res.ok) {
          throw Error('could not fetch the data for that resourse');
        }
        return res.json();
      })
      .then(data => {
        setBooks(data);
        setIsPending(false);
        setError(null);
      })
      .catch(err => {
        setIsPending(false);
        setError(err.message);
      })
  };

  const confirmDelete = (id, title) => {
    setDeletedId(id);
    setDeletedTitle(title);
    setModalIsOpen(true);
  }

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/books/${id}`, {
      method: 'DELETE'
    }).then(() => {
      retriveContacts
    })
  }

  useEffect(() => {
    const getAllBooks = async () => {
      const allBooks = await retriveContacts();
      if (allBooks) setBooks(allBooks);
    };

    getAllBooks();
  }, []);

  // const { data: books, isPending, error } = useFetch('http://localhost:8000/books');

  return (
    <div className="dashboard">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {books && <Dashboard books={books} confirmDelete={confirmDelete} />}
      <Modal
        isOpen={modalIsOpen}
        style={{
          overlay: {
            position: 'fixed',
            top: 0,
            left: 0,
            backgroundColor: 'rgba(111, 255, 255, 0.75)'
          },
          content: {
            position: 'absolute',
            maxWidth: '500px',
            height: '200px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            border: '1px solid #ccc',
            background: '#fff',
            overflow: 'auto',
            WebkitOverflowScrolling: 'touch',
            borderRadius: '4px',
            outline: 'none',
            padding: '20px'
          }
        }}
      >
        <h2 className='modal__header'>Delete {deletedTitle} ?</h2>
        <div className="modal__buttons">
          <button onClick={() => { setModalIsOpen(false); setDeletedTitle(null); setDeletedId(null) }}>No</button>
          <button onClick={() => { setModalIsOpen(false); setDeletedTitle(null); setDeletedId(null); handleDelete(deletedId) }}>Yes</button>
        </div>
      </Modal>
    </div>
  )
}

export default Home
