import React, { useState, useEffect } from 'react';

import Header from '../header/index';
import Main from '../main/index'

import './app.css';

const App = () => {

  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/books')
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data)
        setData(data)
      })
  }, []);

  return (
    <div className="app-container">
      <Header />
      <Main />
    </div>
  )
}

export default App
