import React from 'react';
import { useHistory } from 'react-router-dom';

import './dashboard.sass';

const Dashboard = ({ books }) => {
  const {history} = useHistory();

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/books/${id}`, {
      method: 'DELETE'
    }).then(() => {
      history.push('/');
    })
  }

  return (
    <div>
      <table className='table'>
        <thead>
          <tr>
            <th>Book title</th>
            <th>Author name</th>
            <th>Category</th>
            <th>ISBN</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((item) => {
            return (
              <tr key={item.id}>
                <th>{item.title}</th>
                <th>{item.author}</th>
                <th>{item.category}</th>
                <th>{item.isbn}</th>
                <th>
                <button
                type="button"
                  onClick={() => push(`/edit/${item.id}`)}>
                    Edit
                  </button>
                <button
                  onClick={() => handleDelete(item.id)}>Del</button>
                </th>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default Dashboard
