import React, { useContext } from 'react';
import { UserContext } from '../../context/UserContext';

import './dashboard.sass'


const Dashboard = () => {

  const user = useContext(UserContext);
  const data = user.data

  const handleDelete = (id) => {
    const newBookList = data.filter(book => book.id = id);
    user.setData(newBookList);
  }

  return (
    <div className="dashboard">
      <h1>Book List</h1>
      {
        data.length ?
          <table className='table'>
            <thead>
              <tr>
                <th>Book title</th>
                <th>Author name</th>
                <th>Category</th>
                <th>ISBN</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => {
                return (
                  <tr key={index}>
                    <th>{item.title}</th>
                    <th>{item.author}</th>
                    <th>{item.category}</th>
                    <th>{item.isbn}</th>
                    <th>
                      <button>Ed</button>
                      <button onClick={() => handleDelete(data.id)}>Del</button>
                    </th>
                  </tr>
                )
              })}
            </tbody>
          </table>
          :
          <h2>No books yet</h2>
      }
    </div>
  )
}

export default Dashboard
